module.exports = function (grunt) {
	require('load-grunt-tasks')(grunt);

	grunt.loadNpmTasks('grunt-contrib-uglify');

	grunt.initConfig({
		pkg: grunt.file.readJSON('package.json'),
		uglify: {
			all: {
				files: {
					"transit.min.js": ["transit.js"]
				},
				options: {
					preserveComments: false,
					sourceMap: false,
					report: "min",
					banner: '/*! Espresso Transit v<%= pkg.version %> | ' + //library & version
						'Espresso Logic | ' + //copyright/company
						'http://espressologic.com */\n', //more/register link
					beautify: {
						"ascii_only": true
					},
				}
			}
		}
	});

	grunt.registerTask('default', ['uglify:all']);
	grunt.registerTask('minify', ['uglify:all']);
};